	unsigned char DrawBuffer[128*8]; // display buffer

int32_t main (void)
{
	int i,j;
	UNLOCKREG();
  DrvSYS_SetOscCtrl(E_SYS_XTL12M,1);
  DrvSYS_Delay(20000);				          // Delay for Xtal stable
  while(!SYSCLK->CLKSTATUS.XTL12M_STB);
  DrvSYS_SelectHCLKSource(0);
	LOCKREG();

	init_LCD();  
	clear_LCD();
  
	while(1) {
	   draw_LCD(BMP_Logo_Nuvoton); //  draw_LCD(BMP_Logo);
	   DrvSYS_Delay(500000);
		 
		
		 for (i=0;i<8;i++)
			for (j=0;j<128;j++)
				if (j>64) DrawBuffer[j+i*128]=Academic[(j-64)+i*64];
		    else      DrawBuffer[j+i*128]=0;					 
		 draw_LCD(DrawBuffer);
		 DrvSYS_Delay(500000);
	}

}



